void Solver::bump_var(int var, double coeff) {
    double new_activity = activity[var] + var_inc * coeff;
    if (new_activity <= 1e100) {           // Update score and prevent float overflow
        activity[var] = new_activity;
    } else {
        for (int i = 1; i <= vars; i++) {
            if (i != var) {
                activity[i] *= 1e-100;
            }
        }
        var_inc *= 1e-100;
        activity[var] = var_inc * coeff;
    }
    if (vsids.inHeap(var)) vsids.update(var);                 // update heap
}
